mapper.update:
UPDATE SCHEDULE
SET memo = #{memo}, title = #{title}, String = #{category},
	startDate = #{start_date}, endDate =#{end_date}
WHERE sch_id = #{sch_id}

UPDATE SCHEDULE
SET achievement = #{achievement},  
WHERE sch_id = #{sch_id}

mapper.select:
SELECT sch_id, title, start_date, end_date
FROM Schedule
WHERE u_id = #{u_id}
